import axios from "axios";
export default {
  data() {
    return {
      users: [],
      dialog: false,
      headers: [
        { text: "Opciones", value: "opciones", sortable: false },
        { text: "Name", value: "name", sortable: true },
        { text: "Role", value: "role", sortable: true },
        { text: "Tipodoc", value: "tipodoc",sortable: true },
        { text: "numdoc", value: "numdoc" , sortable: false },
        { text: "password", value: "password", sortable: false },
        { text: "address", value: "address" , sortable: false },
         { text: "phone", value: "phone", sortable: false  },
        { text: "email", value: "email", sortable: false  }
        
      ],
      search: "",
      editedIndex: -1,

      //Model
      id: "",
      name: "",
      idrole: "",
      roles:[],
      tipodoc: "",
      document:['DNI', 'RUC', 'PASAPORTE', 'CEDULA'],
      numdoc: "",
      password: "",
      address: "",
      phone: "",
      email: "",
      actPassword: false,
     passwordAnt:'',
    };
  },
  computed: {
    formTitle() {
      return this.editedIndex === -1 ? "Nuevo User" : "Actualizar User";
    }
  },

  watch: {
    dialog(val) {
      val || this.close();
    }
  },

  created() {
    this.listar();
     this.select()
  },
  methods: {
    listar() {
      let me = this;
      axios
        .get("api/user")
        .then(function(response) {
          //console.log(response);
          me.users = response.data;
        })
        .catch(function(error) {
          console.log(error);
        });
    },
     select(){
               let me = this;
                var rolesArray = []
                axios.get("api/role").then(function(response){
                    rolesArray=response.data
                    rolesArray.map(function(x){
                        me.roles.push({text: x.namerole, value:x.idrol})
                    })
                }).catch(function(error){
                    console.log(error)
                })
            },

    editItem(item) {
      this.id = item.id;
      this.name = item.name;
      this.idrole = item.idrole;
      this.tipodoc = item.tipodoc;
      this.numdoc = item.numdoc;
      this.password = item.passwordhash
      this.passwordAnt = item.passwordhash
     this.phone = item.phone;
      this.email = item.email;

      this.editedIndex = 1;
      this.dialog = true;
    },

  

    close() {
      this.dialog = false;
    },
    limpiar() {
       this.id = "";
      this.name = "";
      this.idrole = "";
      this.tipodoc = "";
      this.numdoc = "";
      this.password = "";
     this.address = "";
     this.phone = "";
      this.email = "";
      this.passwordAnt=""
      this.actPassword=false
    },
      guardar () {
                if(this.validar()){
                    return;
                }
                if (this.editedIndex > -1) {
                    //Para editar
                    let me = this

                    
                    if(me.password!=me.passwordAnt){
                        me.actPassword = true
                    }

                    axios.put('api/Usuarios/Actualizar',{
                        'idusuario':me.id,
                        'idrol':me.idrol,
                        'nombre':me.nombre,
                        'tipo_documento':me.tipo_documento,
                        'num_documento':me.num_documento,
                        'direccion':me.direccion,
                        'telefono':me.telefono,
                        'email':me.email,
                        'password':me.password,
                        'act_password':me.actPassword
                    }).then(function(response){
                        me.close()
                        me.listar()
                        me.limpiar()
                    }).catch(function(error){
                        console.log(error)
                    })
                } else {
                    //Para guardar
                    let me = this

                    axios.post('api/Usuarios/Crear',{
                        'idrol':me.idrol,
                        'nombre':me.nombre,
                        'tipo_documento':me.tipo_documento,
                        'num_documento':me.num_documento,
                        'direccion':me.direccion,
                        'telefono':me.telefono,
                        'email':me.email,
                        'password':me.password
                    }).then(function(response){
                        me.close()
                        me.listar()
                        me.limpiar()
                    }).catch(function(error){
                        console.log(error)
                    })
                }
                this.close()
            },
  }
};
