import axios from "axios";
export default {
  data() {
    return {
      orders: [],
      dialog: false,
      headers: [
                    { text: 'Opciones', value: 'opciones', sortable: false },
                    { text: 'priority', value: 'priority', sortable: false },
                    { text: 'MedicalEquipment', value: 'medicalequipment', sortable: false },
                    { text: 'Description', value: 'description', sortable: false },
                    { text: 'Date', value: 'date', sortable: false },
      ],
      search: "",
      editedIndex: -1,

      //Model
        id: '',
        idmedicalequipment:'',
        medicalequipments:[],
        priority:0,
        descripcion:'',
        date:'',
    };
  },
  computed: {
    formTitle() {
      return this.editedIndex === -1 ? "Nuevo Order" : "Actualizar Order";
    }
  },

  watch: {
    dialog(val) {
      val || this.close();
    }
  },

  created() {
    this.listar();
     this.select()
  },
  methods: {
    listar() {
      let me = this;
      axios
        .get("api/order")
        .then(function(response) {
          //console.log(response);
          me.orders = response.data;
        })
        .catch(function(error) {
          console.log(error);
        });
    },
     select(){
               let me = this;
                var medicalequipmentsArray = []
                axios.get("api/medicalequipment").then(function(response){
                    medicalequipmentsArray=response.data
                    medicalequipmentsArray.map(function(x){
                        me. medicalequipments.push({text: x.name, value:x.id})
                    })
                }).catch(function(error){
                    console.log(error)
                })
            },

    editItem(item) {
      this.id = item.idorder
    this.idmedicalequipment=item.idmedicalequipment
    this.priority=item.priority
     this.description = item.description
     this.date = item.date
              

      this.editedIndex = 1;
      this.dialog = true;
    },

  

    close() {
      this.dialog = false;
    },
    limpiar() {
      this.id=""
                this.idmedicalequipment=""
                this.priority=""
                this.description=""
                this.date=""
                this.editedIndex=-1
    },
      guardar () {
                  console.log("holiwis", this);
                if(this.validar()){
                    return;
                      console.log("hYA");
                }
                if (this.editedIndex > -1) {
                    console.log("holawas");
                    //Para editar
                    let me = this

        
                    axios.put("api/order",{
                        id:me.id,
                        idmedicalequipment:me.idmedicalequipment,
                        priority:me.priority,
                        description:me.description,
                        date:me.date
                    }).then(function(response){
                        me.close()
                        me.listar()
                        me.limpiar()
                    }).catch(function(error){
                        console.log(error)
                    })
                } else {
                      console.log("Jitrusquis");
                    let me = this

                    axios.post("api/order",{
                         idmedicalequipment:me.idmedicalequipment,
                        priority:me.priority,
                        description:me.description,
                        date:me.date
                    }).then(function(response){
                        me.close()
                        me.listar()
                        me.limpiar()
                    }).catch(function(error){
                        console.log(error)
                    })
                }
                this.close()
            },
  }
};
